import UploadIcon from "@mui/icons-material/Upload";
import {
  Dialog,
  DialogContent,
  AppBar,
  Avatar,
  Box,
  Button,
  TextField,
  Toolbar,
  Typography,
} from "@mui/material";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { BackButton } from "../BackButton";
import { useProfile, useAuthUser } from "../data-access";
import { DropzoneDialog } from "material-ui-dropzone";

export const ProfilePage = () => {
  const authUser = useAuthUser();
  const { profile } = useProfile(authUser);

  const [dialogState, setDialogState] = useState<"opened" | "closed">("closed");

  return (
    <>
      <AppBar position="sticky">
        <Toolbar>
          <Link to="/">
            <BackButton edge="start" sx={{ marginRight: 2 }} />
          </Link>
          <Typography variant="h6" sx={{ flex: 1 }}>
            Profile
          </Typography>
        </Toolbar>
      </AppBar>
      <Box
        sx={{
          p: 2,
        }}
      >
        <Box
          sx={{
            marginBottom: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          {JSON.stringify(profile)}
          <Avatar
            sx={{
              width: "132px",
              height: "132px",
              marginBottom: 1,
            }}
            variant="rounded"
          />
          <Button
            fullWidth={false}
            startIcon={<UploadIcon />}
            variant="outlined"
            onClick={() => {
              setDialogState("opened");
            }}
          >
            Upload
          </Button>
          <DropzoneDialog open={dialogState === "opened"} />
        </Box>

        <TextField label="Display Name" sx={{ marginBottom: 4 }} />

        <Button>Save Changes</Button>
      </Box>
    </>
  );
};
