import React from "react";
import { GCU_COLOR, PURPLE_GRADIENT, spacing } from "./theme";
import ContactMailIcon from "@mui/icons-material/ContactMail";
import HomeOutlinedIcon from "@mui/icons-material/HomeOutlined";
import RoomOutlinedIcon from "@mui/icons-material/RoomOutlined";
import HourglassEmptyOutlinedIcon from "@mui/icons-material/HourglassEmptyOutlined";
import MenuIcon from "@mui/icons-material/Menu";

const BOTTOM_ACTIONS = [
  {
    label: "Home",
    Icon: HomeOutlinedIcon,
  },
  {
    label: "Student ID",
    Icon: ContactMailIcon,
  },
  {
    label: "Map",
    Icon: RoomOutlinedIcon,
  },
  {
    label: "Hours",
    Icon: HourglassEmptyOutlinedIcon,
  },
  {
    label: "More",
    Icon: MenuIcon,
  },
];

export const BottomBarAction = ({
  icon,
  label,
}: {
  icon: React.ReactNode;
  label: string;
}) => {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        padding: spacing(1),
        fontSize: "1em",
      }}
    >
      {icon}
      <div style={{ fontSize: "0.8rem", overflow: "hidden" }}> {label}</div>
    </div>
  );
};

export const GCUBottomBar = () => {
  return (
    <div
      style={{
        position: "fixed",
        top: "auto",
        bottom: 0,
        left: 0,
        width: "100vw",
        height: "56px",
        color: "#000",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: GCU_COLOR.white,
      }}
    >
      {BOTTOM_ACTIONS.map((action) => (
        <BottomBarAction
          key={action.label}
          label={action.label}
          icon={action.Icon({ style: { width: "1.2rem" } })}
        />
      ))}
    </div>
  );
};
