import React from "react";
import { GCULogoLong } from "./GCULogoLong";
import { GCU_COLOR, PURPLE_GRADIENT } from "./theme";

const SPACING = "12px";

const Header = () => {
  return (
    <div
      style={{
        background: PURPLE_GRADIENT,
      }}
    >
      <div
        style={{
          margin: "auto",
          padding: SPACING,
          width: "66.66%",
        }}
      >
        <GCULogoLong />
      </div>
    </div>
  );
};

const Footer = () => {
  return (
    <div
      style={{
        background: PURPLE_GRADIENT,
      }}
    >
      <div
        style={{
          textAlign: "center",
        }}
      >
        <p
          style={{
            fontSize: `1em`,
            color: "rgba(255, 255, 255, 0.5)",
            fontStyle: "italic",
            marginBottom: 0,
          }}
        >
          Last Updated:
        </p>
        <p style={{ fontSize: `1em` }}>October 6, 2021, 1:38pm</p>
      </div>
    </div>
  );
};

export const GCUStudentIdCard = ({
  src,
  name,
}: {
  src?: string;
  name?: string;
}) => {
  return (
    <div
      style={{
        background: GCU_COLOR.white,
        height: "100vh",
        padding: SPACING,
      }}
    >
      <div style={{ width: "100%" }}>
        <Header />

        <div style={{ width: "100%", padding: `calc(${SPACING} * 2)` }}>
          <img
            style={{
              width: "80%",
            }}
            src={src}
            alt={name}
          />
          <p style={{ color: "#000" }}>{name}</p>

          <span style={{ backgroundColor: GCU_COLOR.orange }}>Student</span>
        </div>

        <Footer />
      </div>
    </div>
  );
};
