import { updateDoc, doc, onSnapshot, setDoc } from "firebase/firestore";
import { getDownloadURL, ref, uploadBytes } from "firebase/storage";
import { useEffect } from "react";
import create from "zustand";
import { generateId } from "../utils";
import { firestore, storage } from "./firebase";

type IProfile = {
  userId: string;
  displayName?: string;
  profilePictureUrl?: string;
};

type IStore = {
  profile?: Partial<IProfile>;
  setProfile: (profile?: IProfile) => void;
};

const useStore = create<IStore>((set) => ({
  setProfile: (profile) => set({ profile }),
}));

export const useProfile = ({ userId }: { userId: string }) => {
  const { profile, setProfile } = useStore();

  useEffect(() => {
    return onSnapshot(doc(firestore, "profiles", userId), (doc) => {
      const profile = doc.data() as IProfile | undefined;
      setProfile(profile);
    });
  }, [userId, setProfile]);

  const uploadProfilePicture = async (profilePictureFile?: File) => {
    if (profilePictureFile) {
    }
    return null;
  };

  const profileDocRef = doc(firestore, "profiles", profile.userId);

  const updateProfilePicture = async ({
    profilePictureFile,
  }: {
    profilePictureFile: File;
  }) => {
    const storageRef = ref(storage, `profilePictures/${generateId()}`);

    await uploadBytes(storageRef, profilePictureFile);

    const downloadUrl = await getDownloadURL(storageRef);

    await updateDoc(profileDocRef, {
      profilePictureUrl: downloadUrl,
    });
  };

  const updateDisplayName = async ({
    displayName,
  }: {
    displayName: string;
  }) => {
    await updateDoc(profileDocRef, {
      displayName,
    });
  };

  return {
    profile,
    updateProfilePicture,
    updateDisplayName,
  };
};
