import UploadIcon from "@mui/icons-material/Upload";
import { Avatar, Box, Button } from "@mui/material";
import React, { useCallback, useState } from "react";
import { useDropzone } from "react-dropzone";
import { useMutation } from "react-query";
import { useAuthUser, useProfile } from "../data-access";

export const ProfilePictureForm = () => {
  const { userId } = useAuthUser();
  const { profile, updateProfilePicture } = useProfile({ userId });

  const onDrop = useCallback(
    async (acceptedFiles) => {
      const [profilePictureFile] = acceptedFiles;
      if (profilePictureFile) {
        await updateProfilePicture({ profilePictureFile });
      }
    },
    [updateProfilePicture]
  );

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
  });

  return (
    <>
      <Box
        sx={{
          marginBottom: 4,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Avatar
          sx={{
            width: "132px",
            height: "132px",
            marginBottom: 1,
          }}
          variant="rounded"
          src={profile?.profilePictureUrl}
        />

        <div {...getRootProps()}>
          <input {...getInputProps()} />
          <Button
            fullWidth={false}
            startIcon={<UploadIcon />}
            variant="outlined"
          >
            Upload
          </Button>
        </div>
      </Box>
    </>
  );
};
