import UploadIcon from "@mui/icons-material/Upload";
import {
  AppBar,
  Avatar,
  Box,
  Button,
  TextField,
  Toolbar,
  Typography,
} from "@mui/material";
import React from "react";
import { Link } from "react-router-dom";
import { BackButton } from "../BackButton";
import { useProfile, useAuthUser } from "../data-access";

export const AuthUserPage = () => {
  const authUser = useAuthUser();
  const { profile } = useProfile(authUser);
  return (
    <>
      <AppBar position="sticky">
        <Toolbar>
          <Link to="/">
            <BackButton edge="start" sx={{ marginRight: 2 }} />
          </Link>
          <Typography variant="h6" sx={{ flex: 1 }}>
            Profile
          </Typography>
        </Toolbar>
      </AppBar>
      <Box
        sx={{
          p: 2,
        }}
      >
        <Box sx={{ marginBottom: 2, position: "relative" }}>
          {JSON.stringify(profile)}
          <Avatar
            sx={{
              margin: "auto",
              width: "120px",
              height: "120px",
              marginBottom: 1,
            }}
            variant="rounded"
          />
          <Button
            startIcon={<UploadIcon />}
            variant="text"
            sx={{ position: "absolute", bottom: 0 }}
          >
            Edit
          </Button>
        </Box>

        <TextField label="Display Name" sx={{ marginBottom: 4 }} />

        <Button>Save Changes</Button>
      </Box>
    </>
  );
};
