import React from "react";
import { useBarcode } from "react-barcodes";
import { GCULogoLong } from "./GCULogoLong";
import { BORDER_RADIUS, GCU_COLOR, PURPLE_GRADIENT, spacing } from "./theme";
import CachedIcon from "@mui/icons-material/Cached";
import { GCULoadingBar } from "./GCULoadingBar";

const Header = () => {
  return (
    <div
      style={{
        padding: spacing(1),
        background: PURPLE_GRADIENT,
      }}
    >
      <div
        style={{
          margin: "auto",
          // padding: SPACING,
          width: "60%",
          color: "#fff",
        }}
      >
        <GCULogoLong />
      </div>
    </div>
  );
};

const Footer = () => {
  return (
    <div
      style={{
        background: PURPLE_GRADIENT,
        padding: spacing(1),
      }}
    >
      <div
        style={{
          textAlign: "center",
        }}
      >
        <div
          style={{
            fontSize: `1em`,
            color: "rgba(255, 255, 255, 0.5)",
            fontStyle: "italic",
            marginBottom: 0,
          }}
        >
          Last Updated:
        </div>
        <div style={{ fontSize: `1.2em`, fontWeight: "bold", color: "#fff" }}>
          October 6, 2021, 1:38pm
        </div>
      </div>
    </div>
  );
};

export const GCUStudentIdCard = ({
  src,
  name,
}: {
  src?: string;
  name?: string;
}) => {
  const { inputRef } = useBarcode({
    value: "hello w",
    options: {
      height: 48,
      displayValue: false,
    },
  });

  return (
    <div
      style={{
        borderRadius: BORDER_RADIUS,
        overflow: "hidden",
        width: "100%",
        border: `solid 1px ${GCU_COLOR.border}`,
      }}
    >
      <Header />

      <div
        style={{
          width: "100%",
          padding: spacing(1),
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <div
          style={{
            color: GCU_COLOR.purple,
            display: "flex",
            width: "100%",
            flexDirection: "row-reverse",
          }}
        >
          <div
            style={{
              borderRadius: "8px",
              padding: "4px",
              backgroundColor: GCU_COLOR.extraLightPurple,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <CachedIcon />
          </div>
        </div>
        <img
          style={{
            width: "80%",
            aspectRatio: "1",
            objectFit: "cover",
            borderRadius: BORDER_RADIUS,
          }}
          src={src}
          alt={name}
        />
        <div style={{ color: "#000", fontSize: "1.75em", fontWeight: "bold" }}>
          {name}
        </div>

        <span
          style={{
            backgroundColor: GCU_COLOR.orange,
            padding: `0 ${spacing(1 / 2)}`,
            borderRadius: `calc(${BORDER_RADIUS} *  1/2)`,
            fontWeight: "normal",
            color: "#fff",
          }}
        >
          Student
        </span>

        <div style={{ width: "80%", marginBottom: -12 }}>
          <canvas width="100%" ref={inputRef} />
        </div>
      </div>

      <GCULoadingBar />

      <Footer />
    </div>
  );
};
