import UploadIcon from "@mui/icons-material/Upload";
import {
  AppBar,
  Dialog,
  Avatar,
  Box,
  Button,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  Toolbar,
  Typography,
} from "@mui/material";
import Dropzone from "react-dropzone";

import React, { useState } from "react";
import { Link } from "react-router-dom";
import { BackButton } from "../BackButton";
import { useAuthUser, useProfile } from "../data-access";

export const ProfilePage = () => {
  const authUser = useAuthUser();
  const { profile } = useProfile(authUser);

  const [dialogState, setDialogState] = useState<"opened" | "closed">("closed");

  const handleCancel = () => {
    setDialogState("closed");
  };

  return (
    <>
      <AppBar position="sticky">
        <Toolbar>
          <Link to="/">
            <BackButton edge="start" sx={{ marginRight: 2 }} />
          </Link>
          <Typography variant="h6" sx={{ flex: 1 }}>
            Profile
          </Typography>
        </Toolbar>
      </AppBar>
      <Box
        sx={{
          p: 2,
        }}
      >
        <Box
          sx={{
            marginBottom: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          {JSON.stringify(profile)}
          <Avatar
            sx={{
              width: "132px",
              height: "132px",
              marginBottom: 1,
            }}
            variant="rounded"
          />
          <Button
            fullWidth={false}
            startIcon={<UploadIcon />}
            variant="outlined"
            onClick={() => {
              setDialogState("opened");
            }}
          >
            Upload
          </Button>
          <Dialog open={dialogState === "opened"} onClose={handleCancel}>
            <DialogTitle>Upload</DialogTitle>
            <DialogContent>
              <Dropzone onDrop={(acceptedFiles) => console.log(acceptedFiles)}>
                {({ getRootProps, getInputProps }) => (
                  <section>
                    <div {...getRootProps()}>
                      <input {...getInputProps()} />
                      <Box
                        sx={{
                          width: "100vw",
                          maxWidth: "480px",
                          height: "200px",
                        }}
                      ></Box>
                    </div>
                  </section>
                )}
              </Dropzone>
            </DialogContent>
            <DialogActions>
              <Button fullWidth={false} variant="text" onClick={handleCancel}>
                Cancel
              </Button>
              <Button fullWidth={false} variant="text">
                Upload
              </Button>
            </DialogActions>
          </Dialog>
        </Box>

        <TextField label="Display Name" sx={{ marginBottom: 4 }} />

        <Button>Save Changes</Button>
      </Box>
    </>
  );
};
