import {
  addDoc,
  onSnapshot,
  collection,
  query,
  where,
} from "firebase/firestore";
import { useEffect } from "react";
import create from "zustand";
import { firestore, storage } from "./firebase";
import { getStorage, ref, uploadBytes } from "firebase/storage";
import { generateId } from "../utils";

type IProfile = {
  userId: string;
  displayName?: string;
  profilePictureUrl?: string;
};

type IStore = {
  profile?: Partial<IProfile>;
  setProfile: (profile: IProfile) => void;
};

const useStore = create<IStore>((set) => ({
  setProfile: (profile) => set({ profile }),
}));

const profilesRef = collection(firestore, "profiles");

export const useProfile = ({ userId }: { userId: string }) => {
  const { profile, setProfile } = useStore();

  useEffect(() => {
    const profileQuery = query(profilesRef, where("userId", "==", userId));

    const unsubscribe = onSnapshot(profileQuery, (snapshot) => {
      const profiles = snapshot.docs.map((doc) => {
        const data = doc.data();
        return data as IProfile;
      });

      setProfile(profiles[0]);
    });

    return () => {
      unsubscribe();
    };
  }, [userId, setProfile]);

  const addProfile = async ({
    profilePictureFile,
    displayName,
    userId,
  }: {
    displayName: string;
    userId: string;
    profilePictureFile: File;
  }) => {
    const storageKey = `profilePictures/${generateId()}`;

    await uploadBytes(ref(storage, storageKey), profilePictureFile);

    await addDoc(profilesRef, {
      displayName,
      userId,
    });
  };

  return {
    profile,
    addProfile,
  };
};
