import UploadIcon from "@mui/icons-material/Upload";
import {
  AppBar,
  Avatar,
  Box,
  Button,
  TextField,
  Toolbar,
  Typography,
} from "@mui/material";
import React, { useState, useCallback } from "react";
import { useDropzone } from "react-dropzone";
import { Link } from "react-router-dom";
import { BackButton } from "../BackButton";
import { useAuthUser, useProfile } from "../data-access";

export const ProfilePage = () => {
  const authUser = useAuthUser();
  const { profile } = useProfile(authUser);

  const [selectedFile, setSelectedFile] = useState<File | undefined>();
  const [displayName, setDisplayName] = useState<string | undefined>(
    profile?.displayName
  );

  const onDrop = useCallback((acceptedFiles) => {
    const [file] = acceptedFiles;
    if (file) {
      setSelectedFile(file);
    }
  }, []);
  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
  });

  const url = selectedFile ? URL.createObjectURL(selectedFile) : undefined;

  const handleSubmit = () => {
    console.log({ selectedFile, displayName });
  };

  return (
    <>
      <AppBar position="sticky">
        <Toolbar>
          <Link to="/">
            <BackButton edge="start" sx={{ marginRight: 2 }} />
          </Link>
          <Typography variant="h6" sx={{ flex: 1 }}>
            Profile
          </Typography>
        </Toolbar>
      </AppBar>
      <Box
        sx={{
          p: 2,
        }}
      >
        <Box
          sx={{
            marginBottom: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <Avatar
            sx={{
              width: "132px",
              height: "132px",
              marginBottom: 1,
            }}
            variant="rounded"
            src={url}
          />

          <div {...getRootProps()}>
            <input {...getInputProps()} />
            <Button
              fullWidth={false}
              startIcon={<UploadIcon />}
              variant="outlined"
            >
              Upload
            </Button>
          </div>
        </Box>

        <TextField
          label="Display Name"
          sx={{ marginBottom: 4 }}
          onChange={(event) => {
            setDisplayName(event.target.value);
          }}
        />

        <Button onClick={handleSubmit}>Save Changes</Button>
      </Box>
    </>
  );
};
